///@brief Test equality operator of 8 bit signed (likely what we will use for the game) full matrix in default format
TEST(FullMatrixEquality8bit, MatrixEquality) {
    //Yes this example will be narrowed down to fit in 8 bit, but that is not a problem, right now I just treet it as example data
    valueMatrix_dense_full<int8_t> sameMatrix= uniqueEntries<int8_t>();
    EXPECT_TRUE(sameMatrix==sameMatrix);
    //This copies the data into another matrix
    valueMatrix_dense_full<int8_t> otherMatrix(sameMatrix);
    EXPECT_TRUE(sameMatrix==otherMatrix);
    //Alter a single entry, lets just do the first
    otherMatrix[0]=otherMatrix[0]+23/*It is now different*/;
    EXPECT_FALSE(sameMatrix==otherMatrix);
}

///@brief Test equality operator of 16 bit signed full matrix (used for some algorithms) in default format
TEST(FullMatrixEquality16bit, MatrixEquality) {
    valueMatrix_dense_full<int16_t> sameMatrix= uniqueEntries<int16_t>();
    EXPECT_TRUE(sameMatrix==sameMatrix);
    //This copies the data into another matrix
    valueMatrix_dense_full<int16_t> otherMatrix(sameMatrix);
    EXPECT_TRUE(sameMatrix==otherMatrix);
    //Alter a single entry, lets just do the first
    otherMatrix[0]=otherMatrix[0]+23/*It is now different*/;
    EXPECT_FALSE(sameMatrix==otherMatrix);
}

///@brief Test equality operator of unsigned 64 bit full matrix (will not be used for the game, but I like to test that my templates work even in extreme circumstances)
